sys_script_include.script:
var ProductDetails = Class.create();
ProductDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	 getProductDetails:function() {
	   //

              var pcode = this.getParameter('sysparm_pcode');
			  var restMessage = new sn_ws.RESTMessageV2();
				restMessage.setHttpMethod("get");
				restMessage.setEndpoint("https://revademo.pagekite.me/BoxCnC/productdetails?productNum="+pcode);
				var response = restMessage.execute();

				 var responseBody = response.getBody();

             return responseBody;

   } ,
    type: 'ProductDetails'
});
